mapper.update:
UPDATE
exam_testpaper_name
SET
modify_time = #{examTestPaperNameRequest.modifyTime}

    ,test_paper_name = #{examTestPaperNameRequest.testPaperName}


    ,status = #{examTestPaperNameRequest.status}


    ,subject_id = #{examTestPaperNameRequest.subjectId}

WHERE
id = #{examTestPaperNameRequest.id}

update.foreach:
UPDATE
exam_testpaper_content
SET
modify_time = #{examTestPaperContentRequest.modifyTime}
,title = #{examTestPaperContentRequest.title}
,option_a = #{examTestPaperContentRequest.optionA}
,option_b = #{examTestPaperContentRequest.optionB}
,option_c = #{examTestPaperContentRequest.optionC}
,option_d = #{examTestPaperContentRequest.optionD}
,answer = #{examTestPaperContentRequest.answer}
,grade = #{examTestPaperContentRequest.grade}
,type = #{examTestPaperContentRequest.type}
where id = #{examTestPaperContentRequest.id}

mapper.select:
SELECT a.id ,a.test_paper_name testPaperName ,a.subject_id subjectId ,a.STATUS STATUS ,b.subject_name subjectName FROM exam_testpaper_name a LEFT JOIN exam_subject b ON a.subject_id = b.id WHERE a.STATUS = 1
and a.test_paper_name LIKE CONCAT('%',#{request.testPaperName},'%')
ORDER BY a.create_time DESC LIMIT #{offset},#{limit}

SELECT count(1) FROM exam_testpaper_name a INNER JOIN exam_exam b ON a.id = b.exam_testpaper_id INNER JOIN exam_apply_exam c ON b.id = c.exam_id WHERE a.id in
#{ids}
and a.status = 1 and b.status = 1 and c.status = 1

SELECT count(1) FROM exam_testpaper_content x INNER JOIN exam_testpaper_name a ON x.testpaper_name_id = a.id INNER JOIN exam_exam b ON a.id = b.exam_testpaper_id INNER JOIN exam_apply_exam c ON b.id = c.exam_id WHERE a.id IN
#{ids}
and a.status = 1 and b.status = 1 and c.status = 1

SELECT id
FROM exam_testpaper_content
WHERE testpaper_name_id = #{testPaperNameId}

SELECT count(1) FROM exam_exam WHERE exam_testpaper_id in
#{testPaperNameId}
AND NOW() >= start_time AND NOW() <= end_time

SELECT count(1) FROM ( SELECT count(1) FROM exam_testpaper_content a INNER JOIN exam_exam b ON a.testpaper_name_id = b.exam_testpaper_id WHERE now() >= b.start_time AND now() <= b.end_time and a.testpaper_name_id in
#{testPaperNameId}
GROUP BY a.testpaper_name_id ) a
